bot = telebot.TeleBot(TOKEN)

@bot.message_handler(commands=['start'])
def start(message):
    markup = types.ReplyKeyboardMarkup(row_width=3)
    itembtns = []
    for category in pritchi_by_category.keys():
        itembtns.append(types.KeyboardButton(category))
    markup.add(*itembtns)
    bot.send_message(message.chat.id, "–í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é:", reply_markup=markup)

last_sent_index = {}

@bot.message_handler(func=lambda message: True)
def category_selected(message):
    category_name = message.text
    if category_name in pritchi_by_category:
        if category_name not in last_sent_index:
            last_sent_index[category_name] = -1
        last_sent_index[category_name] = (last_sent_index[category_name] + 1) % len(pritchi_by_category[category_name])
        pritcha = pritchi_by_category[category_name][last_sent_index[category_name]]
        formatted_text = f"*{category_name.upper()}*\n\n{pritcha}"  # Using Markdown to make category name bigger
        bot.send_message(message.chat.id, formatted_text, parse_mode="Markdown")
        # If reached the end of the array, reset index to start again
        if last_sent_index[category_name] == 0:
            last_sent_index[category_name] = -1
    else:
        bot.reply_to(message, '–ü—Ä–∏—Ç—á–∏ –ø–æ –¥–∞–Ω–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç.')

bot.polling(none_stop=True)
